mapper.insert:
insert into answer (aid, ahdz, ahd, 
 ahdrq, ahdwt
  )
values (#{aid,jdbcType=INTEGER}, #{ahdz,jdbcType=VARCHAR}, #{ahd,jdbcType=VARCHAR},
  #{ahdrq,jdbcType=DATE}, #{ahdwt,jdbcType=INTEGER}
  )

mapper.update:
update answer
set ahdz = #{ahdz,jdbcType=VARCHAR},
  ahd = #{ahd,jdbcType=VARCHAR},
  ahdrq = #{ahdrq,jdbcType=DATE},
  ahdwt = #{ahdwt,jdbcType=INTEGER}
where aid = #{aid,jdbcType=INTEGER}

mapper.select:
select aid, ahdz, ahd, ahdrq, ahdwt
from answer
where aid = #{aid,jdbcType=INTEGER}

select aid, ahdz, ahd, ahdrq, ahdwt
from answer


    and aid = #{aid,jdbcType=INTEGER}


    and ahdwt = #{ahdwt,jdbcType=INTEGER}

select count(1)
from answer


    and aid = #{aid,jdbcType=INTEGER}


    and ahdwt = #{ahdwt,jdbcType=INTEGER}
