mapper.insert:
insert into answer (ahdz, ahd, ahdwt
,nm
)
values (#{ahdz,jdbcType=VARCHAR}, #{ahd,jdbcType=VARCHAR},#{ahdwt,jdbcType=INTEGER}
,#{nm,jdbcType=VARCHAR}
)

mapper.update:
update answer

    ahdz = #{ahdz,jdbcType=VARCHAR},
    ahd = #{ahd,jdbcType=VARCHAR},
    ahdrq = #{ahdrq,jdbcType=DATE},
    ahdwt = #{ahdwt,jdbcType=INTEGER}
    score = #{score,jdbcType=DOUBLE}
    yzhd = #{yzhd,jdbcType=VARCHAR},
    nm = #{nm,jdbcType=VARCHAR},

where aid = #{aid,jdbcType=INTEGER}

mapper.select:
select aid, ahdz, ahd, ahdrq, ahdwt, score, yzhd, nm
from answer
where aid = #{aid,jdbcType=INTEGER}

select aid, ahdz, ahd, ahdrq, ahdwt, score, yzhd, nm
from answer


    and aid = #{aid,jdbcType=INTEGER}


    and ahdwt = #{ahdwt,jdbcType=INTEGER}


    and ahdz = #{ahdz,jdbcType=VARCHAR}


order by score desc

select count(1)
from answer


    and aid = #{aid,jdbcType=INTEGER}


    and ahdwt = #{ahdwt,jdbcType=INTEGER}


      and ahdz = #{ahdz,jdbcType=VARCHAR}
