mapper.insert:
insert into answer (aid, ahdz, ahd, 
  aztsl, aplsl, ahdrq, ahdwt
  )
values (#{aid,jdbcType=VARCHAR}, #{ahdz,jdbcType=VARCHAR}, #{ahd,jdbcType=VARCHAR}, 
  #{aztsl,jdbcType=INTEGER}, #{aplsl,jdbcType=INTEGER}, #{ahdrq,jdbcType=DATE}, #{ahdwt,jdbcType=VARCHAR}
  )

mapper.select:
select aid, ahdz, ahd, aztsl, aplsl, ahdrq, ahdwt
from answer
where aid = #{aid,jdbcType=VARCHAR}
