mapper.update:
update problem
ptitle = #{ptitle,jdbcType=VARCHAR}, pms = #{pms,jdbcType=VARCHAR}, ptcz = #{ptcz,jdbcType=VARCHAR}, pllsl = #{pllsl,jdbcType=INTEGER}, ptcrq = #{ptcrq,jdbcType=DATE}, pbq = #{pbq,jdbcType=VARCHAR},
where pid = #{pid,jdbcType=INTEGER}

mapper.select:
select pid, ptitle, pms, ptcz, pllsl, ptcrq, pbq
from problem
where pid = #{pid,jdbcType=INTEGER}

select pid, ptitle, pms, ptcz, pllsl, ptcrq, pbq
from problem


        and pid = #{pid,jdbcType=INTEGER}


        and ptcz = #{ptcz,jdbcType=VARCHAR}


order by ptcrq desc

select count(1)
from problem


        and pid = #{pid,jdbcType=INTEGER}


        and ptcz = #{ptcz,jdbcType=VARCHAR}
